// pages/GameOver.js
// ---------------------------------------------------------
// Stand‑alone « Game Over » screen with two pixel‑style buttons.
// It expects a prop `onRetry` to restart the current game.
// ---------------------------------------------------------

import React from "react";
import { useNavigate } from "react-router-dom";
import "../Easy.css"; // réutilise les styles globaux (overlay, pixel-btn, etc.)

export default function GameOver({ onRetry }) {
  const navigate = useNavigate();

  return (
    <div className="overlay-text visible" style={{ flexDirection: "column" }}>
      <h1 className="go-title" style={{ marginBottom: "2rem" }}>
        GAME OVER
      </h1>

      <button className="pixel-btn" onClick={onRetry} style={{ width: "min(90%, 400px)" }}>
        TRY AGAIN
      </button>

      <button
        className="pixel-btn"
        onClick={() => navigate("/")}
        style={{ width: "min(90%, 400px)" }}
      >
        GO BACK TO MENU
      </button>
    </div>
  );
}
