// pages/Easy.js
import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './Easy.css';

/* -------- images -------- */
import cobweb  from '../assets/Images/cobweb.png';
import jokholo from '../assets/Images/jOKHOLO.webp';
import jokerv  from '../assets/Images/JOKERV.webp';

const IMAGES = { jokholo, jokerv };

export default function Easy() {
  /* contiendra l’instance du jeu */
  const gameRef = useRef(null);

  useEffect(() => {
    /* -------- classe MixOrMatch -------- */
    class MixOrMatch {
      constructor(totalTime, cards) {
        this.cardsArray = cards;
        this.totalTime  = totalTime;
        this.timerEl    = document.getElementById('time-remaining');
        this.flipsEl    = document.getElementById('flips');
      }

      startGame() {
        this.cardToCheck   = null;
        this.totalClicks   = 0;
        this.timeRemaining = this.totalTime;
        this.matchedCards  = [];
        this.busy          = true;

        this.flipsEl.textContent = this.totalClicks;
        this.timerEl.textContent = this.timeRemaining;

        this.hideCards();
        this.shuffleCards();

        setTimeout(() => {
          this.busy = false;
          this.countdown = this.startCountdown();
        }, 500);
      }

      startCountdown() {
        return setInterval(() => {
          this.timeRemaining--;
          this.timerEl.textContent = this.timeRemaining;
          if (this.timeRemaining === 0) this.gameOver();
        }, 1000);
      }

      hideCards()  { this.cardsArray.forEach(c => c.classList.remove('visible','matched')); }
      getCardType(card) { return card.querySelector('.card-front').dataset.value; }

      flipCard(card) {
        if (this.canFlipCard(card)) {
          this.totalClicks++;
          this.flipsEl.textContent = this.totalClicks;
          card.classList.add('visible');

          if (!this.cardToCheck) {
            this.cardToCheck = card;
          } else {
            this.checkForCardMatch(card);
          }
        }
      }

      checkForCardMatch(card) {
        const match = this.getCardType(card) === this.getCardType(this.cardToCheck);
        match ? this.cardMatch(card, this.cardToCheck)
              : this.cardMismatch(card, this.cardToCheck);
        this.cardToCheck = null;
      }

      cardMatch(c1, c2) {
        c1.classList.add('matched');
        c2.classList.add('matched');
        this.matchedCards.push(c1, c2);
        if (this.matchedCards.length === this.cardsArray.length) this.victory();
      }

      cardMismatch(c1, c2) {
        this.busy = true;
        setTimeout(() => {
          c1.classList.remove('visible');
          c2.classList.remove('visible');
          this.busy = false;
        }, 800);
      }

      canFlipCard(card) {
        return !this.busy && !this.matchedCards.includes(card) && card !== this.cardToCheck;
      }

      shuffleCards() {
        for (let i = this.cardsArray.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          this.cardsArray[i].style.order = j;
          this.cardsArray[j].style.order = i;
        }
      }

      gameOver() { clearInterval(this.countdown); document.getElementById('GAME-OVER-TEXT').classList.add('visible'); }
      victory()  { clearInterval(this.countdown); document.getElementById('win-text').classList.add('visible'); }
    }

    /* ------------- initialisation ------------- */
    const overlays = Array.from(document.getElementsByClassName('overlay-text'));
    const cards    = Array.from(document.getElementsByClassName('card'));

    overlays[0].classList.add('visible');          // montre “Click to Start”
    gameRef.current = new MixOrMatch(5, cards);   // crée l’instance

    overlays.forEach(ov => {
      ov.addEventListener('click', () => {
        ov.classList.remove('visible');
        gameRef.current.startGame();
      });
    });
    cards.forEach(card =>
      card.addEventListener('click', () => gameRef.current.flipCard(card))
    );
  }, []);

  /* -------- jeu de 12 cartes (6 paires) -------- */
  const cardSet = [
    'jokholo','jokerv','jokholo','jokerv',
    'jokholo','jokerv','jokerv','jokholo',
    'jokerv','jokholo','jokholo','jokerv'
  ];

  return (
    <>
      {/* barre de navigation */}
      <header className="top-bar">
        <Link to="/" className="back-btn">← Back</Link>
      </header>

      <h1 className="page-title">FLOP N MATCH</h1>

      {/* Overlays */}
      <div className="overlay-text">Click to Start</div>

      <div id="GAME-OVER-TEXT" className="overlay-text">
        <h1 className="go-title">GAME OVER</h1>
        <button className="pixel-btn" onClick={() => gameRef.current?.startGame()}>TRY AGAIN</button>
        <button className="pixel-btn" onClick={() => (window.location.href = '/')}>GO BACK TO MENU</button>
      </div>

      <div id="win-text" className="overlay-text">
        <h1 className="go-title">GOOD GAME</h1>
        <button className="pixel-btn" onClick={() => gameRef.current?.startGame()}>PLAY AGAIN</button>
        <button className="pixel-btn" onClick={() => (window.location.href = '/')}>GO BACK TO MENU</button>
      </div>

      {/* Grille + compteurs */}
      <div className="game-container">
        <div className="game-info-container">
          <div className="game-info">Time : <span id="time-remaining">5</span></div>
          <div className="game-info">Flips : <span id="flips">0</span></div>
        </div>

        {cardSet.map((val, idx) => (
          <div className="card" key={idx}>
            <div className="card-back card-face">
              <img src={cobweb} className="cob-web" alt="cobweb" />
            </div>
            <div className="card-front card-face" data-value={val}>
              <img src={IMAGES[val]} className="premier" alt={val} />
            </div>
          </div>
        ))}
      </div>
    </>
  );
}
